--MEMBERS
CREATE TABLE "MEMBERS" 
   (    
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 NOT NULL, 
    "NAME" VARCHAR2(64 BYTE) NOT NULL , 
    "SEX" VARCHAR2(8 BYTE) NOT NULL , 
    "PHONE" NUMBER(12,0), 
    "EMAIL" VARCHAR2(32 BYTE) , 
    "ADDRESS" VARCHAR2(128 BYTE) , 
    "MEMBERSHIP_TYPE" VARCHAR2(32 BYTE) NOT NULL , 
    "RELATED_TO" NUMBER NOT NULL , 
    "RELATION_TYPE" VARCHAR2(16 BYTE) NOT NULL , 
    "MOTHER_PARISH" VARCHAR2(64 BYTE) NOT NULL , 
    "JOINED_DATE" DATE NOT NULL , 
    "BIRTHDAY" DATE NOT NULL , 
    "WEDDING" DATE NOT NULL , 
    "IS_HEAD" VARCHAR2(5 BYTE) 
   );
CREATE INDEX NAME_IDX ON MEMBERS (NAME);
CREATE INDEX PHONE_IDX ON MEMBERS (PHONE);

ALTER TABLE MEMBERS ADD CONSTRAINT CHECK_MEMBERSHIP_TYPE CHECK (MEMBERSHIP_TYPE IN ('PERMANENT', 'DUAL_WITH_VOTING_RIGHT', 'DUAL_WITHOUT_VOTING_RIGHT'));
ALTER TABLE MEMBERS ADD CONSTRAINT CHECK_RELATION_TYPE CHECK (RELATION_TYPE IN ('SELF', 'SPOUSE', 'FATHER', 'MOTHER', 'FATHER_IN_LAW', 'MOTHER_IN_LAW', 'SON', 'DAUGHTER', 'SON_IN_LAW', 'DAUGHTER_IN_LAW', 'GRAND_SON', 'GRAND_DAUGHTER'));
 
      
   